      //     residents: [],
      //     films: [],
      //     created: '',
      //     edited: '',
      //     url: '',

      //   }
      // });
    });
  }



  render() {
    return (
      <React.Fragment>
        <LoggedAppBar></LoggedAppBar>
        <Grid container spacing={3}>
            <Grid item xs={3}>
                <SideBar></SideBar>
            </Grid>
            <Grid item xs={9}>
              {this.state.details.forEach((key, index)=>
              <Grid container spacing={3}>
              <Grid item xs={2}>
                    {this.state.details[index].values}
                </Grid>
                <Grid item xs={9}>
                </Grid>
            </Grid>

              )}

            </Grid>
        </Grid>
      </React.Fragment>
    );
  }
}

export default Details;
