import React, { Component } from 'react';
import '../../index.css';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import LoggedAppBar from '../AppBar';
import SideBar from '../SideBar';
import axios from 'axios';

class Details extends Component {
  constructor() {
    super();
    this.state = {
      details:{
        name: '',
        rotation_period: '',
        orbital_period: '',
        diameter: '',
        climate: '',
        gravity: '',
        terrain: '',
        surface_water: '',
        population: '',
        residents: [],
        films: [],
        created: '',
        edited: '',
        url: '',

      }

    };


  }

  componentWillMount(){
    let url=window.location.href;
    let id=url.split('/');
    id=id[id.length-1];
    axios.get('https://swapi.co/api/planets/'+id+'/').then(res => {
      console.log(res.data);
      this.setState({
        details:{
          name: res.data['name'],
          rotation_period: '',
          orbital_period: '',
          diameter: '',
          climate: '',
          gravity: '',
          terrain: '',
          surface_water: '',
          population: '',
          residents: [],
          films: [],
          created: '',
          edited: '',
          url: '',

        }
      });
    });
  }



  render() {
    return (
      <React.Fragment>
        <LoggedAppBar></LoggedAppBar>
        <Grid container spacing={3}>
            <Grid item xs={3}>
                <SideBar></SideBar>
            </Grid>
            <Grid item xs={9}>
              {this.state.details.map((data, index)=>
              <Grid container spacing={3}>
              <Grid item xs={2}>
                    {data}
                </Grid>
                <Grid item xs={9}>
                    {data}
                </Grid>
            </Grid>

              )}

            </Grid>
        </Grid>
      </React.Fragment>
    );
  }
}

export default Details;
