import React, { Component } from 'react';
import '../../index.css';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import LoggedAppBar from '../AppBar';
import SideBar from '../SideBar';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Home extends Component {
  constructor() {
    super();
    this.state = {
      initialItems : [],
      items: [],
      details:[]
    };

    this.filterList = this.filterList.bind(this);

  }
  handleChange = (index) => {
    axios.get('https://swapi.co/api/planets/'+index+'/').then(res => {
      console.log(res.data);
      let data_set=[];
      //for(let i=0;i<res.data.length;i++){
        for(var key in res.data){
          data_set.push({'key':key, 'value':res.data[key]})  
        }
      this.setState({
        details:data_set
      });
      console.log(this.state.details);

    });
  }

  componentWillMount(){
    axios.get('https://swapi.co/api/planets/').then(res => {
          console.log(res);
          console.log(res.data);
          let itList=[];
          for(let i=0;i<res.data.results.length;i++){
              itList.push(res.data.results[i].name);
          }
          this.setState({
            items: itList,
            initialItems: itList
          });

        });
  }

  filterList(e){
    let updateList = this.state.initialItems;
    updateList = updateList.filter(item => {
      return item.toLowerCase().search(
        e.target.value.toLowerCase()
        ) !== -1;
    });

    this.setState({
      items: updateList
    });
  }

  render() {
    return (
      <React.Fragment>
        <LoggedAppBar></LoggedAppBar>
        <Grid container spacing={3}>
            <Grid item xs={2}>
                <SideBar></SideBar>
            </Grid>
            <Grid item xs={10}>
                <Paper>
                    <input type="text" onChange={this.filterList} />
                    <ul>
                        {this.state.items.map((item, index) => {
                            return<li key={item} id={index}><a href='#' onClick={this.handleChange(index)}>{item}</a></li>
                        })}
                    </ul>

                </Paper>
            </Grid>
        </Grid>
        <Grid container spacing={3}>
        <Grid item xs={9}>
              {this.state.details.map((data, index)=>
              <Grid container spacing={3}>
              <Grid item xs={2}>
                    {data.key}
                </Grid>
                <Grid item xs={9}>
                  {data.value}
                </Grid>
            </Grid>

              )}

            </Grid>
        </Grid>
      </React.Fragment>
    );
  }
}

export default Home;
