import React, { Component } from 'react';
import { render } from 'react-dom';
import '../../index.css';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import LoggedAppBar from '../AppBar';
import SideBar from '../SideBar';
import axios from 'axios';

class Home extends Component {
  constructor() {
    super();
    this.state = {
      initialItems : [],
      items: []
    };

    this.filterList = this.filterList.bind(this);

  }

  componentWillMount(){
    axios.get('https://swapi.co/api/planets/').then(res => {
          console.log(res);
          console.log(res.data);
          let itList=[];
          for(let i=0;i<res.data.results.length;i++){
              itList.push(res.data.results[i].name);
          }
          this.setState({
            items: itList,
            initialItems: itList
          });

        });
  }

  filterList(e){
    let updateList = this.state.initialItems;
    updateList = updateList.filter(item => {
      return item.toLowerCase().search(
        e.target.value.toLowerCase()
        ) !== -1;
    });

    this.setState({
      items: updateList
    });
  }

  render() {
    return (
      <React.Fragment>
        <LoggedAppBar></LoggedAppBar>
        <Grid container spacing={3}>
            <Grid item xs={3}>
                <SideBar></SideBar>
            </Grid>
            <Grid item xs={9}>
                <Paper>
                    <input type="text" onChange={this.filterList} />
                    <ul>
                        {this.state.items.map((item, index) => {
                            return <a href={"details/"+index}><li key={item}>{item}</li></a>
                        })}
                    </ul>

                </Paper>
            </Grid>
        </Grid>
      </React.Fragment>
    );
  }
}

export default Home;
