import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import SendIcon from '@material-ui/icons/Send';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import PeopleOutlineIcon from '@material-ui/icons/PeopleOutline';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import WorkOutlineIcon from '@material-ui/icons/WorkOutline';
import ListIcon from '@material-ui/icons/List';
import { Link } from 'react-router-dom';

const useStyles = makeStyles(theme => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  nested: {
    paddingLeft: theme.spacing(4),
  },
}));

export default function SideBar() {
  const classes = useStyles();
  const [tech, setTech] = React.useState(true);
  const [job, setJob] = React.useState(false);
  const [customer, setCustomer] = React.useState(false);
  const handleClick = () => {
    setTech(!tech);
    setJob(!job);
    setCustomer(!customer);    
  };
  const handleOpenTech = () => {
    setTech(!tech);
  };
  return (
    <List
      component="nav"
      aria-labelledby="nested-list-subheader"
      className={classes.root}
    >
      <ListItem button onClick={handleClick}>
        <ListItemIcon>
          <PeopleOutlineIcon />
        </ListItemIcon>
        <ListItemText primary="Technician" />
        {tech ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={tech} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested} component={Link} to="/home">
            <ListItemIcon>
              <ListIcon />
            </ListItemIcon>
            <ListItemText primary="Technician List" />
          </ListItem>
          <ListItem button className={classes.nested} component={Link} to="/addtechnician">
            <ListItemIcon>
              <PersonAddIcon />
            </ListItemIcon>
            <ListItemText primary="Add Technician" />
          </ListItem>
        </List>
      </Collapse>
      <ListItem button onClick={handleClick}>
        <ListItemIcon>
          <WorkOutlineIcon />
        </ListItemIcon>
        <ListItemText primary="Jobs" />
        {job ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={job} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested} component={Link} to="/jobs">
            <ListItemIcon>
              <ListIcon />
            </ListItemIcon>
            <ListItemText primary="Job List" />
          </ListItem>
          <ListItem button className={classes.nested} component={Link} to="/addjob">
            <ListItemIcon>
              <WorkOutlineIcon />
            </ListItemIcon>
            <ListItemText primary="Add Job" />
          </ListItem>
        </List>
      </Collapse>
      <ListItem button onClick={handleClick}>
        <ListItemIcon>
          <PersonAddIcon />
        </ListItemIcon>
        <ListItemText primary="Customer" />
        {customer ? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={customer} timeout="auto" unmountOnExit>
        <List component="div" disablePadding>
          <ListItem button className={classes.nested} component={Link} to="/customers">
            <ListItemIcon>
              <ListIcon />
            </ListItemIcon>
            <ListItemText primary="Customer List" />
          </ListItem>
          <ListItem button className={classes.nested} component={Link} to="/addcustomer">
            <ListItemIcon>
              <WorkOutlineIcon />
            </ListItemIcon>
            <ListItemText primary="Add Customer" />
          </ListItem>
        </List>
      </Collapse>
      <ListItem button className={classes.nested} component={Link} to="/routes">
        <SendIcon>
          <WorkOutlineIcon />
        </SendIcon>
        <ListItemText primary="Route" />
      </ListItem>
    </List>
  );
}
